RSpec.describe "/trade_parties", type: :request do
  let(:user) { FactoryBot.create(:user) }

  before do
    allow_any_instance_of(ApplicationController).to receive(:current_user) { user }
  end

  # This should return the minimal set of attributes required to create a valid
  # TradeParty. As you add validations to TradeParty, be sure to
  # adjust the attributes here as well.
  let(:valid_attributes) {
    FactoryBot.attributes_for(:trade_party, :minimal)
  }

  let(:invalid_attributes) {
    FactoryBot.attributes_for(:trade_party, :minimal, name: nil)
  }

  describe "GET /index" do
    it "renders a successful response" do
      TradeParty.create! valid_attributes
      get trade_parties_url
      expect(response).to be_successful
    end
  end

  describe "GET /new" do
    it "renders a successful response" do
      get new_trade_party_url
      expect(response).to be_successful
    end
  end

  describe "GET /edit" do
    it "renders a successful response" do
      trade_party = TradeParty.create! valid_attributes
      get edit_trade_party_url(trade_party)
      expect(response).to be_successful
    end
  end

  describe "POST /create" do
    context "with valid parameters" do
      it "creates a new TradeParty" do
        expect {
          post trade_parties_url, params: {trade_party: valid_attributes}
        }.to change(TradeParty, :count).by(1)
      end

      it "redirects to the created trade_party" do
        post trade_parties_url, params: {trade_party: valid_attributes}
        expect(response).to redirect_to(trade_parties_url(params: {locale: :en}))
      end
    end

    context "with invalid parameters" do
      it "does not create a new TradeParty" do
        expect {
          post trade_parties_url, params: {trade_party: invalid_attributes}
        }.to change(TradeParty, :count).by(0)
      end

      it "renders a successful response (i.e. to display the 'new' template)" do
        post trade_parties_url, params: {trade_party: invalid_attributes}
        expect(response).not_to be_successful
      end
    end
  end

  describe "PATCH /update" do
    context "with valid parameters" do
      let(:new_attributes) {
        {name: "New Company"}
      }

      it "updates the requested trade_party" do
        trade_party = TradeParty.create! valid_attributes
        patch trade_party_url(trade_party), params: {trade_party: new_attributes}
        trade_party.reload
        expect(trade_party.name).to eq("New Company")
      end

      it "redirects to the trade_party" do
        trade_party = TradeParty.create! valid_attributes
        patch trade_party_url(trade_party), params: {trade_party: new_attributes}
        trade_party.reload
        expect(response).to redirect_to(trade_parties_url(locale: :en))
      end
    end

    context "with invalid parameters" do
      it "renders a successful response (i.e. to display the 'edit' template)" do
        trade_party = TradeParty.create! valid_attributes
        patch trade_party_url(trade_party), params: {trade_party: invalid_attributes}
        expect(response).not_to be_successful
      end
    end
  end

  describe "DELETE /destroy" do
    it "destroys the requested trade_party" do
      trade_party = TradeParty.create! valid_attributes
      expect {
        delete trade_party_url(trade_party)
      }.to change(TradeParty, :count).by(-1)
    end

    it "redirects to the trade_parties list" do
      trade_party = TradeParty.create! valid_attributes
      delete trade_party_url(trade_party)
      expect(response).to redirect_to(trade_parties_url(params: {locale: :en}))
    end
  end
end
